import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';

const GetTitle = ({films}) => {
  console.log(films)

  const [getTitle, setGetTitle] = useState([films])

  const {info} = useParams();

  useEffect(()=> {
    setGetTitle()
  },[]) // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <div>GetOriginTitle component 
    <ul> {
      getTitle.filter((filmPopular, idx) => filmPopular.title.toLowerCase().includes(info.toLowerCase())).map((filmPopular) => {
       console.log(filmPopular)
        return (
            <li key={filmPopular.idx} >
            <p>{filmPopular.original_title}</p>
            <img src={"https://image.tmdb.org/t/p/w200" + filmPopular.poster_path} alt="" />
            </li>
        ) 
      })}
    </ul>
    </div>
  )
}

export default GetTitle
