import React, { useState } from 'react'
import { useParams } from 'react-router-dom';

const GetTitle = ({films}) => {
  console.log(films)

  const {search} = useParams();
  console.log(search)

  return (
    <div>GetTitle component 
    <ul> {
      films.filter((filmPopular, idx) => filmPopular.title.toLowerCase().includes(search.toLowerCase())).map((filmPopular) => {
       console.log(filmPopular)
        return (
            <li key={filmPopular.idx} >
            <p>{filmPopular.title}</p>
            <img src={"https://image.tmdb.org/t/p/w200" + filmPopular.poster_path} alt="" />
            </li>
        ) 
      })}
    </ul>
    </div>
  )
}

export default GetTitle
