import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';

const GetTitle = () => {

  const [films, setFilms] = useState([]);
  const {info} = useParams();

  useEffect(()=> {
    getApiInfo()
  },[]) // eslint-disable-line react-hooks/exhaustive-deps


  //Get info Api and add to state
  const getApiInfo = async () => {
    let url = `https://api.themoviedb.org/3/movie/popular?api_key=fa97f60d1d3864cbc1194358693ca4b5&language=es-ES`;
    const response = await fetch(url);
    const res = await response.json();
    // console.log(res.results)
    setFilms(res.results)
  }

  return (
    <section className='section--getTitle'>
    <h2 className='section--getTitle-title'>Title</h2>
    <ul className='section--getTitle-list'>{
      films.filter((filmPopular, idx) => filmPopular.overview.toLowerCase().includes(info.toLowerCase())).map((filmPopular) => {
        return (
            <li key={filmPopular.idx} >
            <p>{filmPopular.original_title}</p>
            <img src={"https://image.tmdb.org/t/p/w200" + filmPopular.poster_path} alt="" />
            <p>{filmPopular.overview}</p>
            </li>
        ) 
      })}
    </ul>
    </section>
  )
}

export default GetTitle
